INSERT INTO empresa (nombre, fundacion, historia, img) VALUES
('Nintendo', '1889', 'Empezo como una empresa de juguetes que se volvio una empresa de videojuegos en los 70s, su impacto de hizo dramaticamente grandes empezando en los 80s', 'https://upload.wikimedia.org/wikipedia/commons/9/95/Nintendo_Logo_2017.png'),
('Sony', '1946', 'Se unio a las consolas despues de que Nintendo rompio su contrato, ya que obtuvieron informacion util de ello, desde entonces ha sido un gran jugador en la industria con su marca PlayStation', ''),
('Microsoft', '1975', 'Debido a que percibio a Sony como una amenaza debido al exito de la PlayStation 2 y su capacidad de correr linux, se unio a la industria del videojuego con su marca Xbox en 2001', '');
